    private void VBalokActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_VBalokActionPerformed
        // TODO add your handling code here:
        int panjang, lebar, tinggi;
        panjang = Integer.parseInt(PBalok.getText());
        lebar = Integer.parseInt(LBalok.getText());
        tinggi = Integer.parseInt(TBalok.getText());
        int hasil= panjang*lebar*tinggi;

        try{
            JOptionPane.showMessageDialog(null,"Volume Balok Adalah : "+hasil,"Volume",
                JOptionPane.INFORMATION_MESSAGE);
        }catch (Exception e){
            JOptionPane.showMessageDialog(null,"ERROR:"+e,"GAGAL",JOptionPane.WARNING_MESSAGE);
        }
    }//GEN-LAST:event_VBalokActionPerformed

    private void VKubusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_VKubusActionPerformed
        // TODO add your handling code here:
        int sisi;
        sisi = Integer.parseInt(SKubus.getText());
        int hasil =sisi*sisi*sisi;

        try{
            JOptionPane.showMessageDialog(null,"Volume Kubus Adalah : "+hasil,"Volume",
                JOptionPane.INFORMATION_MESSAGE);
        }catch (Exception e){
            JOptionPane.showMessageDialog(null,"ERROR:"+e,"GAGAL",JOptionPane.WARNING_MESSAGE);
        }
    }//GEN-LAST:event_VKubusActionPerformed

