    private void VBolaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_VBolaActionPerformed
        // TODO add your handling code here:
        int jari;
        jari= Integer.parseInt(JBola.getText());
        float hasil =(float) (4*Math.PI*jari*jari*jari/3);

        try{
            JOptionPane.showMessageDialog(null,"Volume Bola Adalah : "+hasil,"Volume",
                JOptionPane.INFORMATION_MESSAGE);
        }catch (Exception e){
            JOptionPane.showMessageDialog(null,"ERROR:"+e,"GAGAL",JOptionPane.WARNING_MESSAGE);
        }
    }//GEN-LAST:event_VBolaActionPerformed

